'use client';

import Image from 'next/image';

interface ChampionImageProps {
  src: string;
  alt: string;
  fallbackSrc: string;
  className?: string;
  priority?: boolean;
  sizes?: string;
}

export function ChampionImage({
  src,
  alt,
  fallbackSrc,
  className,
  priority,
  sizes,
}: ChampionImageProps) {
  return (
    <Image
      src={src}
      alt={alt}
      fill
      className={className}
      priority={priority}
      sizes={sizes}
      quality={85}
      placeholder="blur"
      blurDataURL="data:image/jpeg;base64,"
      onError={e => {
        const target = e.currentTarget as HTMLImageElement;
        target.src = fallbackSrc;
      }}
    />
  );
}
